mapper.select:
SELECT users_name
FROM tbl_users
WHERE users_number = #{usersNumber}

SELECT users_postsal_code
FROM   tbl_users
WHERE  users_number = #{usersNumber}

SELECT  users_address_line1
FROM   tbl_users
WHERE  users_number = #{usersNumber}

SELECT  users_address_line2
FROM   tbl_users
WHERE  users_number = #{usersNumber}

SELECT users_phone
FROM   tbl_users
WHERE  users_number = #{usersNumber}

		SELECT users_id
		FROM   tbl_users
    	WHERE users_number = #{usersNumber}

SELECT users_email
FROM   tbl_users
WHERE users_number = #{usersNumber}

		SELECT users_password
		FROM   tbl_users
    	WHERE users_number = #{usersNumber}

mapper.update:
		UPDATE tbl_users
		SET users_postsal_code  = #{usersPostsalCode},
    		users_address_line1 = #{usersAddressLine1},
    		users_address_line2 = #{usersAddressLine2}
		WHERE users_number = #{usersNumber}

UPDATE tbl_users
SET users_phone  = #{usersPhone}
WHERE users_number = #{usersNumber}

UPDATE tbl_users
SET users_email  = #{usersEmail}
WHERE users_number = #{usersNumber}

update tbl_users 
set users_password = #{usersPassword}
where tbl_users.users_number = #{usersNumber}
