mapper.select:
			SELECT u.users_number, r.reference_number, r.reference_title, u.users_name, r.reference_created_date, r.reference_content
			FROM   tbl_reference r
			JOIN   tbl_users u
  			ON   r.reference_users_number = u.users_number
			WHERE  u.users_number = #{usersNumber}
  			AND  r.reference_number = #{referenceNumber};
