mapper.select:

		SELECT * FROM (
			SELECT ROWNUM AS rnum, subquery.*
			FROM (
				SELECT u.users_number, r.reference_number, u.users_name, r.reference_title, r.reference_created_date, r.reference_answered_yn
			FROM   tbl_reference r
			JOIN   tbl_users u
  			ON   r.reference_users_number = u.users_number
			WHERE  u.users_number = #{usersNumber}
			ORDER BY r.reference_created_date DESC
			) subquery
		) WHERE rnum BETWEEN #{startRow} AND #{endRow}
