mapper.select:
SELECT COUNT(USERS_PASSWORD)
FROM TBL_USERS
WHERE USERS_PASSWORD = #{usersPassword}

mapper.update:
	UPDATE tbl_users
    SET users_postsal_code = #{usersPostCode},
        users_address_line1 = #{usersAddrLine1},
        users_address_line2 = #{usersAddrLine2},
        users_email = #{usersEmail}
    WHERE users_number = #{usersNumber}

	UPDATE tbl_normal_users
    SET normal_users_level = #{normalUsersLevel}
    WHERE users_number = #{usersNumber}

mapper.delete:
DELETE FROM TBL_SHOPS_FAVORITES f
WHERE f.favorites_number = #{favoritesNumber}
	AND f.users_number = (
		SELECT u.users_number
        FROM tbl_users u
        WHERE u.user_number = #{usersNumber}
	);

DELETE FROM tbl_posts
WHERE posts_number = #{postsNumber}
	AND users_number = #{usersNumber};
