mapper.select:
SELECT u.users_number
FROM tbl_users u
WHERE u.users_id = #{usersId}
AND u.users_password = #{usersPassword}
AND u.users_type = 'N'

SELECT u.users_id
FROM tbl_users u


		WHERE u.users_name = #{usersName}
		AND u.users_phone =
		#{usersPhone}
		AND u.users_type = 'N'


		WHERE u.users_name = #{usersName}
		AND u.users_phone =
		#{usersPhone}
		AND u.users_type = 'C'


FETCH FIRST 1 ROWS ONLY

